class Solution {
public:
    long long countSubarrays(vector<int>& sequence, int target) {
        int length = sequence.size();
        long long subCnt = 0;
        unordered_map<long long, long long> prevResults;

        for (int index = 0; index < length; ++index) {
            unordered_map<long long, long long> currentResults;

            if (sequence[index] == target) {
                ++subCnt;
            }
            currentResults[sequence[index]] = 1;

            for (const auto& entry : prevResults) {
                long long value = entry.first;
                long long frequency = entry.second;
                long long newAndResult = value & sequence[index];
                if (newAndResult == target) {
                    subCnt += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            prevResults = std::move(currentResults);
        }

        return subCnt;
    }
};
