class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        total_count = 0
        previous_results = defaultdict(int)

        for i in range(n):
            current_results = defaultdict(int)

            if nums[i] == k:
                total_count += 1

            current_results[nums[i]] = 1

            for value, freq in previous_results.items():
                new_result = value & nums[i]
                if new_result == k:
                    total_count += freq
                current_results[new_result] += freq

            previous_results = current_results

        return total_count
