class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n = nums.size();
    long long fn = 0;
 unordered_map<long long, long long>p;


    for (int i = 0; i < n; ++i) {
        unordered_map<long long, long long>c; 
       c[nums[i]]=1;
       if (nums[i] == k) 
               fn++;

        for (auto &it : p) {
         long long r = (it.first)&nums[i];
             c[r]+=it.second;
            if(r==k)fn+=it.second;


        }
        p=c;

    }
        return fn;
    }
};
