class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        long long n = nums.size();
        map <long long, long long> mp;
        for(int num : nums){
            map <long long, long long> mp2;

            vector <int> temp;

            if(num == k)ans++;
            mp2[num] = 1;
            for(auto x : mp){
                long long val = x.first;
                long long freq = x.second;
                long long a = val & num;
                if(a == k){
                    ans += freq;
                }
                mp2[a] += freq;
            }
            mp = mp2;
        }
        return ans;
    }
};
