       class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cou = 0;

        unordered_map<long long, long long> p;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                ++cou;
            }
            curr[nums[i]] = 1;

            for (auto& [value, frequ] : p) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    cou += frequ;
                }
                curr[newAndResult] += frequ;
            }

            p = curr;
        }

        return cou;
    }
};
