class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        prev_sub_arrays=Counter()
        res=0
        for n in nums:
            if n==k:res+=1
            prev_sub_arrays_temp=Counter([n])
            for key,v in prev_sub_arrays.items():
                prev_sub_arrays_temp[key&n]+=v
                if key&n==k:res+=v
            prev_sub_arrays=prev_sub_arrays_temp

        return res
