class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long total = 0;
        unordered_map<long long, long long> prefixSums;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> currentSums;
            if (nums[i] == k) {
                ++total;
            }
            currentSums[nums[i]] = 1;

            for (auto& [value, frequency] : prefixSums) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    total += frequency;
                }
                currentSums[newAndResult] += frequency;
            }

            prefixSums = currentSums;
        }
        return total;

    }
};
