class Solution {
public:
    long long countSubarrays(vector<int>& color, int k) {
        int n = color.size();


        long long ans = 0;


        unordered_map<long long, long long> mp1;

        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> mp2; 
            if (color[i] == k) {
                ans++;
            }
            mp2[color[i]] = 1;
            for (auto& [val, freq] : mp1) {
                long long newResult = val & color[i];
                if (newResult == k) {
                    ans += freq;
                }
                mp2[newResult] += freq;
            }

            mp1 = mp2;
        }
        return ans;
    }
};
