class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;
        Map<Integer, Integer> seen = new HashMap<>();

        for(int i = 0; i < n; i++) {
            Map<Integer, Integer> temp = new HashMap<>();
            if(nums[i] == k) {
                count++;
            }
            temp.put(nums[i], 1);

            for(var e : seen.entrySet()) {
                int key = e.getKey();
                int val = e.getValue();

                int num = (key & nums[i]);
                if(num == k) {
                    count += val;
                }

                if(temp.containsKey(num)) {
                    temp.put(num, temp.get(num) + val);
                } else {
                    temp.put(num, val);
                }
            }

            seen = temp;
        }

        return count;
    }
}
