class Solution {
public:
    unordered_map<long long, long long> mp;
    unordered_map<long long, long long> mp1;
    void increment(int a,int b,int ans)
    {
        if(a==b)
            ans++;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            mp1.clear();
            int l = nums[i];
            if (l == k) {
                //increment(a,b,ans)
                ans++;
            }
            mp1[l] = 1;
            /* void increment(int a,int b,int ans)
    {
        if(a==b)
            ans++;
    }*/
            for (auto& it : mp) {
                long long g = it.first & l;
                if (g == k) {
                    //increment(a,b,ans)
                    ans =ans+ it.second;
                }
                mp1[g] = mp1[g]+it.second;
            }

            mp = mp1;
        }
        return ans;
    }
};
