class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long total = 0;
        unordered_map<long long, long long> pResult;
        for (int index = 0; index < n; ++index) {
            unordered_map<long long, long long> cResult;
            if (nums[index] == k) {
                ++total;
            }
            cResult[nums[index]] = 1;
            for (auto& [value, frequency] : pResult) {
                long long newAndResult = value & nums[index];
                if (newAndResult == k) {
                    total += frequency;
                }
                cResult[newAndResult] += frequency;
            }

            pResult = cResult;
        }
        return total;
    }
};
