class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> prevans;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> crntans; 
            if (array[index] == target) {
                ++total;
            }
            crntans[array[index]] = 1;
            for (auto& [value, frequency] : prevans) {
                long long newans = value & array[index];
                if (newans == target) {
                    total += frequency;
                }
                crntans[newans] += frequency;
            }

            prevans = crntans;
        }
        return total;
    }
};
