class Solution {
public:
    long long res=0;
    unordered_map<long long, long long>mp;
    long long countSubarrays(vector<int>& nums, int k) {
    int n=nums.size();
    for (int i = 0; i < n; i++) {
        unordered_map<long long, long long> curr; 
        if (nums[i] == k) {
            res++;
        }
        curr[nums[i]] = 1; 
        for (auto g : mp) {
            long long v=g.first;
            long long f=g.second;
            long long And = v & nums[i];
            if (And == k) {
                res += f;
            }
            curr[And] += f;
        }

        mp = curr;
    }

    return res;
    }
};
