class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> m; 

            if (nums[i] == k) {
                count++;
            }
            m[nums[i]] = 1;

            for (auto& [val, fq] : mp) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += fq;
                }
                m[newAndResult] += fq;
            }

            mp = m;
        }

        return count;
    }
};
