class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        int n = nums.size();
        unordered_map<int, int> andCount;

        for (int num : nums) {
            unordered_map<int, int> newAndCount;

            if (num == k) {
                count++;
            }

            for (auto& [andVal, freq] : andCount) {
                int newAndVal = andVal & num;
                if (newAndVal == k) {
                    count += freq;
                }
                newAndCount[newAndVal] += freq;
            }

            newAndCount[num]++;

            andCount = move(newAndCount);
        }

        return count;
    }
};
