typedef long long ll;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll count = 0;

        unordered_map<int, int> aFreq;
        int i ;
        for ( i = 0; i < n; ++i) {
            unordered_map<int, int> tempFreq;
            if (nums[i] == k) {
                ++count;
            }
            tempFreq[nums[i]] = 1;

            for (auto& [val, freq] : aFreq) {
                int newAnd = val & nums[i];
                if (newAnd == k) {
                    count += freq;
                }
                tempFreq[newAnd] += freq;
            }

            aFreq = tempFreq;
        }

        return count;
    }
};
