class Solution {
    #define ll long long
public:
    long long countSubarrays(vector<int>& nums, int target) {
        int n = nums.size();
        ll ans = 0;
        unordered_map<ll, ll> previous;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> current; 

            if (nums[i] == target) {
                ++ans;
            }

            current[nums[i]] = 1;

            for (auto& [value, freq] : previous) {
                ll new_and = value & nums[i];

                if (new_and==target) {
                    ans += freq;
                }

                current[new_and] += freq;
            }

            previous = current;
        }

        return ans;
   }
};
