class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long c = 0;
        unordered_map<long long, long long> huehue;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> cu; 
            if (nums[index] == k) {
                ++c;
            }
            cu[nums[index]] = 1;
            for (auto& [value, f] : huehue) {
                long long n = value & nums[index];
                if (n == k) {
                    c += f;
                }
                cu[n] += f;
            }

            huehue = cu;
        }
        return c;
    }
};
