class Solution {
public:
    int dp[100010][32];
    long long countSubarrays(vector<int>& nums, int k) {
        for(int i=0;i<=30;i++){
            for(int j=0;j<nums.size();j++){
                if(((nums[j]>>i)&1)==1){
                    if(j==0) dp[j][i]=1;
                    else dp[j][i]=dp[j-1][i]+1;
                }else dp[j][i]=0;
            }
        }
        int l=0;
        long long res=0;
        for(int i=0;i<nums.size();i++){
            bool p=true;
            int left=i+1,right=0;
            for(int j=0;j<=30;j++){
                int a=(k>>j)&1,b=(nums[i]>>j)&1;
              //  cout<<k<<" "<<b<<endl;
                if(a==1&&b==0){
                    p=false;
                    break;
                }else if(a==1&&b==1){
                    left=min(left,dp[i][j]);
                }else if(a==0&&b==1){
                    right=max(right,dp[i][j]);
                }
            }
           // cout<<p<<endl;
            if(p&&left>=right){
                cout<<"a";
                cout<<left<<" "<<right<<endl;
                res=res+(left-right);
            }
        }
        return res;
    }
};
