class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0, n = nums.size();
        unordered_map<int, int> pre;
        for(int i = 0; i < n; i++){
            unordered_map<int, int> tmp;
            tmp[nums[i]] ++;
            if(nums[i] == k) ans += 1;
            // cout << "i 为" << i << endl;
            // for(auto &[key, num]: pre){
            //     cout << key << " : " << num << ' ';
            // }
            // cout << endl;
            for(auto &[key, num]: pre){
                int now = nums[i] & key;
                // cout << "now is " << now << endl;
                if(now == k) ans += num;
                if(now >= k) tmp[now] += num;
            }
            pre = tmp;
        }
        // if(kn) ans += (kn * (kn + 1)) / 2;
        return ans;
    }
};
