class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
        map<long long int,int> mpp;
        long long int ans = 0;   
        for(int i = 0; i<nums.size(); i++)
        {
            map<long long int, int>freq;
            if(nums[i]==k)
                ans++;
            freq[nums[i]]++;
            for(auto &it: mpp)
            {
                long long int temp = it.first&nums[i];
                if(temp==k)
                    ans = ans + it.second;
                freq[temp] = freq[temp] + it.second;
            }
            mpp.clear();
            mpp = freq;
        }
        return ans;
    }
};
