class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> m;
        int i=0;
          while(i<nums.size()){
            unordered_map<long long, long long> v; 

            if (nums[i] == k) {
                ++ans;
            }
            v[nums[i]] = 1;

            for (auto i1: m) {
                long long result = i1.first & nums[i];
                if (result == k) {
                    ans += i1.second;
                }
                v[result] += i1.second;
            }

            m = v;
            i++;
        }

        return ans;

    }
};
