class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> freq;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> new_freq;
            new_freq[nums[i]]++;
            for (auto& [val, cnt] : freq) {
                int new_val = val & nums[i];
                new_freq[new_val] += cnt;
            }
            if (new_freq.count(k)) {
                count += new_freq[k];
            }
            freq = new_freq;
        }

        return count;
    }
};
