class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        function<int(int,int)> findL=[&](int l,int r)
        {
            if(l>r)
                return n;
            int m=(l+r)/2;
            if(nums[m]==k)
                return min(m,findL(l,m-1));
            else if(nums[m]<k)
                return findL(m+1,r);
            else
                return findL(l,m-1);
        };
        function<int(int,int)> findR=[&](int l,int r)
        {
            if(l>r)
                return -1;
            int m=(l+r)/2;
            if(nums[m]==k)
                return max(m,findR(m+1,r));
            else if(nums[m]<k)
                return findR(m+1,r);
            else
                return findR(l,m-1);
        };
        for (int i = 0; i < nums.size(); i++) {
            int x = nums[i];
            for (int j = i - 1; j >= 0; j--) {
                if((nums[j] & x) == nums[j])
                    break;
                nums[j] &= x;
            }
            int l=findL(0,i);
            int r=findR(0,i);
            //cout<<i<<" "<<l<<" "<<r<<endl;
            if(r!=-1)
                ans+=r-l+1;
        }
        return ans;
    }
};
