class Solution {
    public long countSubarrays(int[] nums, int k) {
        int len = nums.length;

        long answer = 0;
        Map<Long, Long> map = new HashMap<>();
        for (int i=0; i<len; ++i) {
            Map<Long, Long> temp= new HashMap<>();
            if (nums[i] == k) {
                ++answer;
            }
            temp.put((long)nums[i], 1L);
            for (Long key:map.keySet()) {
                Long v = map.get(key);
                long t = key & nums[i];
                if (t == k) {
                    answer += v;
                }
                temp.put((long)t, temp.getOrDefault(t, 0L) + v);
            }
            map = temp;
        }
        return answer;
    }
}
