class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        flag = [[]]
        for x in nums:
            if x & k == k:
                flag[-1].append(x)
            else:
                if len(flag[-1]) > 0:
                    flag.append([])
        if len(flag[-1]) == 0:
            flag.pop()
        ans = 0
        for f in flag:
            i = 0
            j = 0
            ss = 0
            while i < len(f):
                now = f[i]
                while now != k:
                    j += 1
                    if j >= len(f):
                        break
                    now = now & f[j]
                if now != k:
                    break
                now = f[j]

                for jj in range(j, i - 1, -1):
                    now = now & f[jj]
                    if now == k:
                        break

                ans += ((jj - i + 1) * (len(f) - j))
                i = jj + 1
                ss = i
                j = i
        return ans
