class Solution:
    def countSubarrays(self, arr: List[int], k: int) -> int:
        n = len(arr)
        total = 0
        prev = defaultdict(int)

        for i in range(n):
            curr = defaultdict(int)

            if arr[i] == k:
                total += 1

            curr[arr[i]] = 1

            for val, freq in prev.items():
                new_result = val & arr[i]

                if new_result == k:
                    total += freq

                curr[new_result] += freq

            prev = curr

        return total
