class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0,n=nums.size(),pre=-1;
        vector<long long> dp(34,-1);
        for(int i=0;i<n;i++){
            if((nums[i]&k)!=k){
                pre=i;
                continue;
            }
            long long j=i;
            long long a=nums[i],b=k;
            for(int wei=0;wei<34;wei++){
                //cout<<a<<" "<<b<<endl;
                if((a%2==1)&&(b%2==1)) {
                    a/=2;
                    b/=2;
                    continue;
                }
                if(a%2){
                    j=min(j,dp[wei]);
                }else{
                    dp[wei]=i;
                }
                a/=2;
                b/=2;
            }
            if(j==-1||j<pre) continue;
            ans=ans+(j-pre);
            //cout<<i<<" "<<j-pre<<" "<<j<<" "<<pre<<endl;
        }
        return ans;
    }
};
