import java.util.*;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;

        Map<Long, Long> prev= new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> curr = new HashMap<>();

            if (nums[i] == k) {
                ++count;
            }
            curr.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : prev.entrySet()) {
                long newAndResult = entry.getKey() & nums[i];
                if (newAndResult == k) {
                    count += entry.getValue();
                }
                curr.put(newAndResult, curr.getOrDefault(newAndResult, 0L) + entry.getValue());
            }

            prev = curr;
        }

        return count;
    }
}
