class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         long long TotaL = 0;
        int sZe = nums.size();
        unordered_map<long long, long long> Pre;
        for (int i = 0; i < sZe; ++i) {
            unordered_map<long long, long long> Curr; 
            if (nums[i] == k) {
                ++TotaL;
            }
            Curr[nums[i]] = 1;
            for (auto& [value, frequency] : Pre) {
                long long Result = value & nums[i];
                if (Result == k) {
                  TotaL += frequency;
                }
                Curr[Result] += frequency;
            }

            Pre = Curr;
        }
        return TotaL;

    }
};
