class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
       int n = nums.size();
        long long totalSubarrays = 0;
        unordered_map<long long, long long> prevCounts;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currCounts;

            if (nums[i] == target) {
                ++totalSubarrays;
            }

            currCounts[nums[i]] = 1;

            for (auto& [val, freq] : prevCounts) {
                long long newAndResult = val & nums[i];

                if (newAndResult == target) {
                    totalSubarrays += freq;
                }

                currCounts[newAndResult] += freq;
            }

            prevCounts = currCounts;
        }

        return totalSubarrays;


    }
};
