typedef long long ll;
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        ll nd = 0;
        unordered_map<ll, ll> mpp;
        for (int i = 0; i < size; i++) {
            unordered_map<ll, ll> curr; 
            if (nums[i] == k) nd++;

            curr[nums[i]] = 1;
            for (auto& [val, cnt] : mpp) {
                ll x = val & nums[i];
                if (x == k) nd += cnt;
                curr[x] += cnt;
            }
            mpp = curr;
        }
        return nd;
    }
};
