class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;

        unordered_map<long long, long long> andFreq;

        for (int i=0; i<n; ++i) {
            unordered_map<long long, long long> curr;

            curr[nums[i]]=1;
            if (nums[i]==k) {
                ++count;
            }

            for (auto& [val, freq]: andFreq) {
                long long newAndResult=val&nums[i];
                curr[newAndResult]+=freq;
                if (newAndResult==k) {
                    count+=freq;
                }
            }

            andFreq=curr;
        }

        return count;

    }
};
