class Solution {
public:

    int celuelri(){
        return 0;
    }
    long long countSubarrays(vector<int>& nums, int k) {

        long long count = 0;
        unordered_map<int,int> trackBack;
        celuelri();

        for(int n:nums){
            unordered_map<int,int> trackCur;
            trackCur[n]++;

            for(const auto &it:trackBack){

                celuelri();

                int value = it.first;
                int freq = it.second;
                int newVal = value & n;

                trackCur[newVal] += freq;
            }
            celuelri();
            if(trackCur.find(k) != trackCur.end()){
                count += trackCur[k];
            }
            trackBack = trackCur;
        }
        celuelri();
        return count;
    }
};
