#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll,ll> fr;
        ll ans = 0;
        ll n = nums.size();
        for(int j = 0;j<n;j++){
            map<ll,ll> ff;
            for(auto &&i:fr){
                ff[nums[j]&(i.first)] += i.second;
            }
            ff[nums[j]]++;
            ans += ff[k];
            fr = ff;
        }
        return ans;
    }
};
