class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<long long, long long> mp;
        long long cnt=0;
        for(int i=0; i<n; i++){
            map<long long, long long> m;
            if(nums[i]==k)cnt++;
            m[nums[i]]=1;
            for(auto x:mp){
                long long val=x.first&nums[i];
                if(val==k){
                    cnt+=x.second;
                }
                m[val]+=x.second;
            }
            mp=m;
        }
        return cnt;
    }
};
