class Solution {
public:
    long long countSubarrays(vector<int>& nums, int x) {
        int n=nums.size();
        vector<int> lst0(30, n), lst1(30, n);
        long long ans=0;
        for(int i=n-1; i>=0; i--){
            for(int k=0; k<30; k++){
                if(nums[i]&(1<<k)) lst1[k]=i;
                else lst0[k]=i;
            }
            int l=i, r=n;
            for(int k=0; k<30; k++){
                if(x&(1<<k)) r=min(r, lst0[k]);
                else l=max(l, lst0[k]);
            }
            // cout << "lr " << l << " " << r << '\n';
            ans+=max(0, r-l);
        }
        return ans;
    }
};
