class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, long long> mp;
        long long ans = 0;
        for (int x : nums) {
            unordered_map<int, long long> mp1;
            mp[(long long)(1<<31)-1] = 1;
            for (auto it : mp) {

                // cout << it.first<<endl;cout << x <<endl;
                int val = (it.first & x);
                // if (!mp[val])
                mp1[val] += it.second;
                // cout << val << ' ' << mp1[val] << endl;
            }
            // cout<<endl;
            ans += mp1[k];
            mp = mp1;
        }
        return ans;
    }
};
