class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res=int(nums[0]==k)
        temp=defaultdict(int)
        temp[nums[0]]=1
        for num in nums[1:]:
            t=defaultdict(int)
            for i in temp:
                t[i&num]+=temp[i]
            t[num]+=1
            temp=t
            res+=temp[k]
        return res
