class Solution {
 public:
  long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    long long ans = 0;
    vector<array<int, 3>> andNums;
    for (int i = 0; i < n; i++) {
      andNums.push_back({i, i, -1});
      int cnt = 0;
      for (int j = 0; j < andNums.size(); j++) {
        andNums[j][2] = andNums[j][2] & nums[i];
        if (cnt && andNums[cnt - 1][2] == andNums[j][2])
          andNums[cnt - 1][1] = andNums[j][1];
        else
          andNums[cnt++] = andNums[j];
      }
      andNums.resize(cnt);
      for (auto& it : andNums)
        if (it[2] == k) ans += it[1] - it[0] + 1;
    }
    return ans;
  }
};
