class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        map<int,int> tmp,now;
        for(int i=0;i<n;i++){
            now=tmp;
            tmp.clear();
            for(auto &[x,y]:now){
                if((x&nums[i])==k) {
                    tmp[k]+=y;
                    ans+=y;
                }else{
                    tmp[x&nums[i]]+=y;
                }
            }
            if(nums[i]==k) ans++;
            tmp[nums[i]]++;
        }
        return ans;
    }
};
