class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) { 
        int n = nums.size(); 
        long long ans = 0; 
        map<long long,long long> mp; 
        for (int i = 0; i < n; ++i) 
        { 
            map<long long,long long> np;  
            if (nums[i] == k) 
            { 
                ans++; 
            } 
            np[nums[i]] = 1; 
            for (auto& [val, c] : mp) 
            { 
                long long t = val & nums[i]; 
                if (t == k) 
                { 
                    ans += c; 
                } 
                np[t] += c; 
            } 
            mp = np; 
        } 
        return ans; 
    } 
};
