class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        unordered_map<long long, long long> dp;

        for (int i = 0; i < nums.size(); ++i) {
            unordered_map<long long, long long> new_dp;

            for (auto& pair : dp) {
                long long new_and = pair.first & nums[i];
                if (new_and == k) {
                    count += pair.second;
                }
                if (new_and >= k) {
                    new_dp[new_and] += pair.second;
                }
            }

            if (nums[i] == k) {
                count++;
            }
            if (nums[i] >= k) {
                new_dp[nums[i]]++;
            }

            dp = new_dp;
        }

        return count;
    }
};
