#define rep(i, n) for (int i = 0; i < (n); ++i)
#define Rep(i, n) for (int i = 1; i <= (n); ++i)
#define clr(x, a) memset(x, (a), sizeof x)
int const N = 100100;
typedef long long ll;

int nz[N][33];
int cur[33];
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        rep(i, 30) cur[i] = n;
        for (int i = n - 1; i >= 0; --i) {
            rep(j, 30) {
                if (!(a[i] >> j & 1)) cur[j] = i;
                nz[i][j] = cur[j];
            }
        }
        ll ans = 0;
        rep(i, n) {
            int x = a[i];
            int cur = i;
            while (cur < n) {
                int t = n;
                rep(j, 30) {
                    if (x >> j & 1) {
                        t = min(t, nz[cur][j]);
                    }
                } 
               // cout<<i<<" "<<cur << " " << t<<"="<<x<<endl;
                if (x == k) ans += t - cur;
                cur = t;
                if (t >= n) break;
                x &= a[t];
            }
        }
        return ans;
    }
};
