class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        vector<array<int, 2>> u;
        u.push_back({0, -1});
        ll ans = 0;
        int n = nums.size();
        for(int i=0; i<n; i++){
            vector<array<int, 2>> t;
            int o = nums[i];
            int w = i;
            for(auto [z, j]: u){
                if((z&o)!=o){
                    if(o==k)ans+=w-j;
                    t.push_back({o, w});
                    w=j;
                }
                o&=z;
            }
            if(o==k)ans+=w+1;
            t.push_back({o, w});
            swap(t, u);
        }
        return(ans);
    }
};
