class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int arraySize = array.size();
        long long countOfSubarrays = 0;
        map<long long, long long> previousAndResults;

        for (int currentIndex = 0; currentIndex < arraySize; ++currentIndex) {
            map<long long, long long> currentAndResults;
            if (array[currentIndex] == target) {
                ++countOfSubarrays;
            }
            currentAndResults[array[currentIndex]] = 1;
            for (auto& [andResult, count] : previousAndResults) {
                long long newAndResult = andResult & array[currentIndex];
                if (newAndResult == target) {
                    countOfSubarrays += count;
                }
                currentAndResults[newAndResult] += count;
            }
            previousAndResults = currentAndResults;
        }

        return countOfSubarrays;
    }
};
