package main

import (
	"fmt"
)

func countSubarrays(nums []int, k int) int64 {
	n := len(nums)
	count := int64(0)
	prevResults := make(map[int64]int64)

	for i := 0; i < n; i++ {
		currResults := make(map[int64]int64)

		if nums[i] == k {
			count++
		}
		currResults[int64(nums[i])] = 1

		for val, freq := range prevResults {
			newAndResult := val & int64(nums[i])
			if newAndResult == int64(k) {
				count += freq
			}
			currResults[newAndResult] += freq
		}

		prevResults = currResults
	}

	return count
}
