class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, int> mp;
        long long ans = 0;
        for(int i = 0; i < n; ++i)
        {
            map<int, int> new_mp;
            new_mp[nums[i]]++;
            for(auto it : mp)
            {
                new_mp[it.first & nums[i]] += it.second;
            }
            ans += new_mp[k];
            mp = new_mp;
        }
        return ans;
    }
};
