class Solution {
public:
#define ll long long

    int f(vector<vector<int>>&ct, int l , int r){

        int ans = 0;

        for(int i = 0; i<30; i++){

            if(ct[r][i] - ct[l - 1][i] == r - l + 1){
                ans += (1<<i);
            }
        }
        // cout<<"r l "<<r<<" "<<l<<" ans "<<ans<<"\n";
        return ans;
    }
    ll func(vector<int>& nums, int k){
        // cout<<"k "<<k<<"\n";
        ll ans = 0;
        int n = nums.size();
        vector<vector<int>>ct(n+1,vector<int>(30,0)); 

        for(int i = 0; i < n; i++){
            bitset<30>b(nums[i]);
            ct[i+1] = ct[i];
            for(int j = 0; j < 30; j++) ct[i+1][j] += b[j];
        }

        int j = 0;

        for(int i = 0;i < n; i++){

            while(j <= i && f(ct, j+1, i+1) < k)
            {
                // cout<<" i j get "<<f(ct, i+1, j+1)<<"\n";
                j++;
            }
            // cout<<i<<" "<<j<<"\n";
            ans  += i - j + 1;
        }

        return ans;

    }
    long long countSubarrays(vector<int>& nums, int k) {


        return  func(nums, k) - func(nums, k+1);
    }
};
