class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int nex[30];
        for(int j = 0;j <= 29;j++) nex[j] = -1;
        int n = nums.size();
    	long long ans = 0;
    	int max_range = n - 1;
        for(int i = n - 1;i >= 0;i--){
        	if((nums[i] & k) == k){//可行
        		bool f = 1;
        		int nx = i;
        		for(int j = 0;j <= 29;j++){
        			if(((nums[i] >> j) & 1) == 0){
        				nex[j] = i;
        			}else if(((k >> j) & 1) == 0){
        				nx = max(nx,nex[j]);
        				if(nex[j] == -1) f = 0;
        			}
        		}
        		if(f){
        			ans += (max_range - nx + 1);
        		}
        	}else{
        		for(int j = 0;j <= 29;j++) nex[j] = -1;//不可
        		max_range = i - 1;
        	}
        }
        return ans;
    }
};
